class AppLocalizations {
  final Locale locale;

  AppLocalizations(this.locale);
  static LanguageEntity of(BuildContext context) {
    return Localizations.of<AppLocalizations>(context, AppLocalizations).languageEntity;
  }

  static const LocalizationsDelegate<AppLocalizations> delegate = AppLocalizationsDelegate();

  LanguageEntity languageEntity;

  Future<LanguageEntity> loadTest() async {
    Map<String, dynamic> jsonMap = await json.decode(jsonLang);
    languageEntity = LanguageEntity().fromJson(jsonMap);
    return languageEntity;
  }

  Future<bool> load() async {
    String jsonString = await rootBundle.loadString('i18n/${locale.languageCode}.json');
    Map<String, dynamic> jsonMap = await json.decode(jsonString);
    languageEntity = LanguageEntity().fromJson(jsonMap);
    return true;
  }
}

class AppLocalizationsDelegate extends LocalizationsDelegate<AppLocalizations> {
  const AppLocalizationsDelegate();
  @override
  bool isSupported(Locale locale) {
    return ['en', 'th'].contains(locale.languageCode);
  }

  @override
  Future<AppLocalizations> load(Locale locale) async {
    final appLocale = AppLocalizations(locale);
    await appLocale.load();
    return appLocale;
  }

  @override
  bool shouldReload(AppLocalizationsDelegate old) => false;
}

class AppLocalizationsTestDelegate extends LocalizationsDelegate<AppLocalizations> {
  const AppLocalizationsTestDelegate();
  @override
  bool isSupported(Locale locale) {
    return ['en', 'th'].contains(locale.languageCode);
  }

  @override
  Future<AppLocalizations> load(Locale locale) async {
    return SynchronousFuture<AppLocalizations>(AppLocalizations(
      locale,
    ));
  }

  @override
  bool shouldReload(AppLocalizationsDelegate old) => false;
}
